interface IHintsProps {
    hintLines: number[][];
    finishedLines: boolean[];
    gameRunning: boolean;
    classIdentifier: string;
}

const Hints = ({
    hintLines,
    finishedLines,
    gameRunning,
    classIdentifier,
}: IHintsProps) => {
    return (
        <div className={classIdentifier}>
            {hintLines.map((hintLine, i) => {
                return (
                    <div key={i}>
                        {!gameRunning || finishedLines[i] ? (
                            <div></div>
                        ) : (
                            hintLine.map((hint, index) => (
                                <div
                                    key={index}
                                    className={
                                        finishedLines[i] ? "solved" : "number"
                                    }
                                >
                                    {hint}
                                </div>
                            ))
                        )}
                    </div>
                );
            })}
        </div>
    );
};
